@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
    font-family: Inter, system-ui, sans-serif;
  }

  h1, h2, h3, h4, h5, h6 {
    @apply font-semibold;
  }
}

/* Dashboard Canvas Styling */
.dashboard-canvas {
  background-image: radial-gradient(circle, hsl(var(--muted)) 1px, transparent 1px);
  background-size: 20px 20px;
  position: relative;
  min-height: 600px;
}

/* Draggable Card Styling */
.draggable-card {
  cursor: move;
  transition: all 0.2s ease;
}

.draggable-card:hover {
  transform: translateY(-2px);
  box-shadow: 0 10px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04);
}

.dark .draggable-card:hover {
  box-shadow: 0 10px 25px -5px rgba(255, 255, 255, 0.1), 0 10px 10px -5px rgba(255, 255, 255, 0.04);
}

/* Chart Container */
.chart-container {
  position: relative;
  height: 200px;
  width: 100%;
}

/* Sidebar Navigation */
.sidebar-nav {
  transition: transform 0.3s ease-in-out;
}

@media (max-width: 768px) {
  .sidebar-nav.closed {
    transform: translateX(-100%);
  }
}

/* Custom scrollbar for webkit browsers */
::-webkit-scrollbar {
  width: 8px;
  height: 8px;
}

::-webkit-scrollbar-track {
  background: hsl(var(--muted));
  border-radius: 4px;
}

::-webkit-scrollbar-thumb {
  background: hsl(var(--muted-foreground) / 0.3);
  border-radius: 4px;
}

::-webkit-scrollbar-thumb:hover {
  background: hsl(var(--muted-foreground) / 0.5);
}

/* Animation keyframes */
@keyframes fadeIn {
  from {
    opacity: 0;
    transform: translateY(10px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes slideInFromLeft {
  from {
    opacity: 0;
    transform: translateX(-20px);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
}

@keyframes pulse {
  0%, 100% {
    opacity: 1;
  }
  50% {
    opacity: 0.5;
  }
}

/* Utility classes for animations */
.animate-fade-in {
  animation: fadeIn 0.3s ease-out;
}

.animate-slide-in-left {
  animation: slideInFromLeft 0.3s ease-out;
}

.animate-pulse-slow {
  animation: pulse 2s infinite;
}

/* Card hover effects */
.card-hover {
  transition: all 0.2s ease;
}

.card-hover:hover {
  transform: translateY(-2px);
  box-shadow: 0 4px 12px -2px rgba(0, 0, 0, 0.1);
}

.dark .card-hover:hover {
  box-shadow: 0 4px 12px -2px rgba(255, 255, 255, 0.1);
}

/* Status indicator styles */
.status-indicator {
  display: inline-flex;
  align-items: center;
  gap: 0.5rem;
}

.status-indicator::before {
  content: '';
  width: 8px;
  height: 8px;
  border-radius: 50%;
  background-color: currentColor;
  opacity: 0.8;
}

/* Form focus styles */
.form-input {
  transition: all 0.2s ease;
}

.form-input:focus {
  outline: none;
  ring: 2px;
  ring-color: hsl(var(--ring));
  border-color: hsl(var(--ring));
}

/* Button styles */
.btn-primary {
  background-color: hsl(var(--primary));
  color: hsl(var(--primary-foreground));
  border: none;
  transition: all 0.2s ease;
}

.btn-primary:hover {
  background-color: hsl(var(--primary) / 0.9);
}

.btn-secondary {
  background-color: hsl(var(--secondary));
  color: hsl(var(--secondary-foreground));
  border: none;
  transition: all 0.2s ease;
}

.btn-secondary:hover {
  background-color: hsl(var(--secondary) / 0.8);
}

/* Loading spinner */
.spinner {
  animation: spin 1s linear infinite;
}

@keyframes spin {
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
}

/* Data visualization styles */
.chart-tooltip {
  background-color: hsl(var(--card));
  border: 1px solid hsl(var(--border));
  border-radius: var(--radius);
  box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1);
  padding: 0.5rem;
  color: hsl(var(--card-foreground));
}

/* Error state styles */
.error-state {
  color: hsl(var(--destructive));
  background-color: hsl(var(--destructive) / 0.1);
  border: 1px solid hsl(var(--destructive) / 0.2);
  border-radius: var(--radius);
  padding: 1rem;
}

/* Success state styles */
.success-state {
  color: hsl(var(--chart-2));
  background-color: hsl(var(--chart-2) / 0.1);
  border: 1px solid hsl(var(--chart-2) / 0.2);
  border-radius: var(--radius);
  padding: 1rem;
}

/* Dark mode specific adjustments */
.dark .dashboard-canvas {
  background-image: radial-gradient(circle, hsl(var(--muted) / 0.5) 1px, transparent 1px);
}

/* Responsive design helpers */
@media (max-width: 640px) {
  .dashboard-canvas {
    background-size: 15px 15px;
  }
}

/* Print styles */
@media print {
  .sidebar-nav,
  .no-print {
    display: none !important;
  }

  .dashboard-canvas {
    background: none !important;
  }
}

