import { useQuery } from "@tanstack/react-query";
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, LineChart, Line } from "recharts";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { BarChart3, TrendingUp, Table as TableIcon } from "lucide-react";
import { useEffect, useState } from "react";

interface ChartRendererProps {
  type: string;
  config: any;
  dataSourceId: string | null;
}

export function ChartRenderer({ type, config, dataSourceId }: ChartRendererProps) {
  const [refreshInterval, setRefreshInterval] = useState<number | false>(false);

  // Get data source info to determine refresh settings
  const { data: dataSourceInfo } = useQuery({
    queryKey: ["/api/data-sources", dataSourceId],
    enabled: !!dataSourceId,
  });

  // Calculate refresh interval in milliseconds
  useEffect(() => {
    if (dataSourceInfo?.config?.refreshInterval && dataSourceInfo?.config?.refreshUnit) {
      const interval = dataSourceInfo.config.refreshInterval;
      const unit = dataSourceInfo.config.refreshUnit;

      let milliseconds = 0;
      switch (unit) {
        case 'seconds':
          milliseconds = interval * 1000;
          break;
        case 'minutes':
          milliseconds = interval * 60 * 1000;
          break;
        case 'hours':
          milliseconds = interval * 60 * 60 * 1000;
          break;
        case 'days':
          milliseconds = interval * 24 * 60 * 60 * 1000;
          break;
        case 'weeks':
          milliseconds = interval * 7 * 24 * 60 * 60 * 1000;
          break;
        case 'months':
          milliseconds = interval * 30 * 24 * 60 * 60 * 1000; // Approximate
          break;
        default:
          milliseconds = 5 * 60 * 1000; // Default 5 minutes
      }

      // Only enable auto-refresh for reasonable intervals (minimum 10 seconds)
      setRefreshInterval(milliseconds >= 10000 ? milliseconds : false);
    } else {
      setRefreshInterval(false);
    }
  }, [dataSourceInfo]);

  const { data, isLoading, error } = useQuery({
    queryKey: ["/api/data-sources", dataSourceId, "data"],
    enabled: !!dataSourceId,
    refetchInterval: refreshInterval,
  });

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-full bg-muted/50 rounded-md">
        <div className="text-center">
          <div className="animate-spin w-6 h-6 border-2 border-primary border-t-transparent rounded-full mx-auto mb-2"></div>
          <p className="text-sm text-muted-foreground">Loading data...</p>
        </div>
      </div>
    );
  }

  if (error || !dataSourceId) {
    return (
      <div className="flex items-center justify-center h-full bg-muted/50 rounded-md">
        <div className="text-center">
          <div className="w-8 h-8 bg-destructive/10 rounded-full flex items-center justify-center mx-auto mb-2">
            <span className="text-destructive text-sm">!</span>
          </div>
          <p className="text-sm text-muted-foreground">
            {error ? "Failed to load data" : "No data source configured"}
          </p>
        </div>
      </div>
    );
  }

  // Render based on visualization type
  switch (type) {
    case "table":
      return (
        <div className="h-full bg-muted/50 rounded-md">
          {(data as any)?.data?.length > 0 ? (
            <div 
              className="h-full overflow-auto scrollbar-thin scrollbar-track-muted scrollbar-thumb-muted-foreground"
              style={{
                scrollbarWidth: 'thin',
                scrollbarColor: 'hsl(var(--muted-foreground)) hsl(var(--muted))'
              }}
            >
              <table className="border-collapse" style={{ minWidth: 'max-content' }}>
                <thead className="[&_tr]:border-b sticky top-0 bg-muted/50">
                  <tr className="border-b transition-colors hover:bg-muted/50">
                    {(data as any).fields?.map((field: string) => {
                      const displayName = (data as any).fieldDisplayNames?.[field] || field;
                      return (
                        <th 
                          key={field}
                          className="h-8 px-4 text-left align-middle font-medium text-muted-foreground whitespace-nowrap min-w-[120px]"
                        >
                          {displayName}
                        </th>
                      );
                    })}
                  </tr>
                </thead>
                <tbody className="[&_tr:last-child]:border-0">
                  {(data as any).data.map((row: any, index: number) => (
                    <tr key={index} className="border-b transition-colors hover:bg-muted/50">
                      {(data as any).fields?.map((field: string) => {
                        const value = row[field];
                        let displayValue = value;

                        // Handle nested objects by converting to string
                        if (typeof value === 'object' && value !== null) {
                          displayValue = JSON.stringify(value);
                        } else if (value === null || value === undefined) {
                          displayValue = '';
                        }

                        return (
                          <td 
                            key={field}
                            className="p-4 align-middle whitespace-nowrap text-sm min-w-[120px]"
                          >
                            {String(displayValue)}
                          </td>
                        );
                      })}
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ) : (
            <div className="flex items-center justify-center h-full">
              <div className="text-center">
                <TableIcon className="w-8 h-8 text-muted-foreground mx-auto mb-2" />
                <p className="text-sm text-muted-foreground">No data available</p>
              </div>
            </div>
          )}
        </div>
      );

    case "chart":
      return (
        <div className="h-full bg-muted/50 rounded-md p-2">
          {(data as any)?.data?.length > 0 ? (
            <ResponsiveContainer width="100%" height="100%">
              <BarChart data={(data as any).data}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="name" fontSize={12} />
                <YAxis fontSize={12} />
                <Tooltip />
                <Bar dataKey="value" fill="hsl(var(--primary))" />
              </BarChart>
            </ResponsiveContainer>
          ) : (
            <div className="flex items-center justify-center h-full">
              <div className="text-center">
                <BarChart3 className="w-8 h-8 text-muted-foreground mx-auto mb-2" />
                <p className="text-sm text-muted-foreground">No data available</p>
              </div>
            </div>
          )}
        </div>
      );

    case "graph":
      return (
        <div className="h-full bg-muted/50 rounded-md p-2">
          {(data as any)?.data?.length > 0 ? (
            <ResponsiveContainer width="100%" height="100%">
              <LineChart data={(data as any).data}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="name" fontSize={12} />
                <YAxis fontSize={12} />
                <Tooltip />
                <Line type="monotone" dataKey="value" stroke="hsl(var(--primary))" strokeWidth={2} />
              </LineChart>
            </ResponsiveContainer>
          ) : (
            <div className="flex items-center justify-center h-full">
              <div className="text-center">
                <TrendingUp className="w-8 h-8 text-muted-foreground mx-auto mb-2" />
                <p className="text-sm text-muted-foreground">No data available</p>
              </div>
            </div>
          )}
        </div>
      );

    default:
      return (
        <div className="flex items-center justify-center h-full bg-muted/50 rounded-md">
          <div className="text-center">
            <div className="w-8 h-8 bg-muted rounded-lg flex items-center justify-center mx-auto mb-2">
              <span className="text-muted-foreground text-sm">?</span>
            </div>
            <p className="text-sm text-muted-foreground">Unknown visualization type</p>
          </div>
        </div>
      );
  }
}

client/src/components/dashboard/dashboar.content:
import { useState, useRef, useCallback } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { DashboardCard } from "./dashboard-card";
import { Button } from "@/components/ui/button";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { ArrowLeft, Plus, Save, BarChart3, Table, TrendingUp } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import type { Dashboard, DashboardCard as DashboardCardType, DataSource } from "@shared/schema";

interface DashboardCanvasProps {
  dashboard: Dashboard;
  onBack: () => void;
  isPublic?: boolean;
}

export function DashboardCanvas({ dashboard, onBack, isPublic = false }: DashboardCanvasProps) {
  const [isAddCardDialogOpen, setIsAddCardDialogOpen] = useState(false);
  const [isEditCardDialogOpen, setIsEditCardDialogOpen] = useState(false);
  const [editingCard, setEditingCard] = useState<DashboardCardType | null>(null);
  const [cardTitle, setCardTitle] = useState("");
  const [selectedDataSource, setSelectedDataSource] = useState("");
  const [selectedVisualizationType, setSelectedVisualizationType] = useState("");
  const canvasRef = useRef<HTMLDivElement>(null);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: cards = [], isLoading } = useQuery({
    queryKey: isPublic 
      ? ["/api/public/dashboards", dashboard.id, "cards"]
      : ["/api/dashboards", dashboard.id, "cards"],
  });

  const { data: dataSources = [] } = useQuery({
    queryKey: ["/api/data-sources"],
    enabled: !isPublic, // Only fetch data sources for authenticated dashboard editing
  });

  const createCardMutation = useMutation({
    mutationFn: (data: any) =>
      apiRequest("POST", `/api/dashboards/${dashboard.id}/cards`, data),
    onSuccess: () => {
      toast({
        title: "Card created",
        description: "Your dashboard card has been created successfully.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/dashboards", dashboard.id, "cards"] });
      setIsAddCardDialogOpen(false);
      setCardTitle("");
      setSelectedDataSource("");
      setSelectedVisualizationType("");
    },
    onError: () => {
      toast({
        title: "Failed to create card",
        description: "Please try again.",
        variant: "destructive",
      });
    },
  });

  const updateCardMutation = useMutation({
    mutationFn: ({ cardId, updates }: { cardId: string; updates: any }) =>
      apiRequest("PUT", `/api/cards/${cardId}`, updates),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/dashboards", dashboard.id, "cards"] });
    },
    onError: () => {
      toast({
        title: "Failed to update card",
        description: "Please try again.",
        variant: "destructive",
      });
    },
  });

  const editCardMutation = useMutation({
    mutationFn: (data: any) =>
      apiRequest("PUT", `/api/cards/${editingCard?.id}`, data),
    onSuccess: () => {
      toast({
        title: "Card updated",
        description: "Your dashboard card has been updated successfully.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/dashboards", dashboard.id, "cards"] });
      setIsEditCardDialogOpen(false);
      setCardTitle("");
      setSelectedDataSource("");
      setSelectedVisualizationType("");
      setEditingCard(null);
    },
    onError: () => {
      toast({
        title: "Failed to update card",
        description: "Please try again.",
        variant: "destructive",
      });
    },
  });

  const visualizationTypes = [
    { id: "table", name: "Table", description: "Display data in rows and columns", icon: Table },
    { id: "chart", name: "Bar Chart", description: "Show data as bars", icon: BarChart3 },
    { id: "graph", name: "Line Graph", description: "Display trends over time", icon: TrendingUp },
  ];

  const handleAddCard = () => {
    setIsAddCardDialogOpen(true);
  };

  const handleEditCard = (card: DashboardCardType) => {
    setEditingCard(card);
    setCardTitle(card.title);
    setSelectedDataSource(card.dataSourceId || "");
    setSelectedVisualizationType(card.visualizationType);
    setIsEditCardDialogOpen(true);
  };

  const saveDashboardMutation = useMutation({
    mutationFn: (updates: any) =>
      apiRequest("PUT", `/api/dashboards/${dashboard.id}`, updates),
    onSuccess: () => {
      toast({
        title: "Dashboard saved",
        description: "Your dashboard has been saved successfully.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/dashboards"] });
    },
    onError: () => {
      toast({
        title: "Failed to save dashboard",
        description: "Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleSaveDashboard = () => {
    saveDashboardMutation.mutate({
      name: dashboard.name,
      description: dashboard.description,
      isPublic: dashboard.isPublic,
      updatedAt: new Date().toISOString(),
    });
  };


  const handleCardPositionChange = useCallback((cardId: string, position: { x: number; y: number }, size?: { width: number; height: number }) => {
    const updates: any = { position };
    if (size) {
      updates.size = size;
    }
    updateCardMutation.mutate({ cardId, updates });
  }, [dashboard.id, updateCardMutation]);

  const handleCreateCard = (e: React.FormEvent) => {
    e.preventDefault();
    if (!cardTitle.trim() || !selectedVisualizationType) {
      toast({
        title: "Validation error",
        description: "Please fill in all required fields.",
        variant: "destructive",
      });
      return;
    }

    // Calculate position for new card (simple grid layout)
    const existingCardsCount = (cards as DashboardCardType[]).length;
    const x = (existingCardsCount % 3) * 320; // 300px width + 20px margin
    const y = Math.floor(existingCardsCount / 3) * 240; // 200px height + 40px margin

    createCardMutation.mutate({
      title: cardTitle.trim(),
      dataSourceId: selectedDataSource === "none" ? null : selectedDataSource || null,
      visualizationType: selectedVisualizationType,
      position: { x, y },
      size: { width: 300, height: 200 },
      config: {},
    });
  };

  const handleUpdateCard = (e: React.FormEvent) => {
    e.preventDefault();
    if (!cardTitle || !selectedVisualizationType || !editingCard) return;

    editCardMutation.mutate({
      title: cardTitle,
      dataSourceId: selectedDataSource === "none" ? null : selectedDataSource,
      visualizationType: selectedVisualizationType,
      config: {},
    });
  };

  return (
    <div className="flex-1 flex flex-col overflow-hidden">
      {!isPublic && (
        <div className="bg-card border-b border-border p-6">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4">
              <Button 
                variant="ghost" 
                size="sm" 
                onClick={onBack}
                className="text-muted-foreground hover:text-foreground"
                data-testid="button-back-to-dashboards"
              >
                <ArrowLeft className="w-4 h-4" />
              </Button>
              <h3 className="text-xl font-semibold text-foreground" data-testid="text-dashboard-name">
                {dashboard.name}
              </h3>
            </div>
            <div className="flex items-center space-x-2">
              <Button 
                variant="secondary" 
                size="sm"
                onClick={handleAddCard}
                data-testid="button-add-card"
              >
                <Plus className="w-4 h-4 mr-2" />
                Add Card
              </Button>
              <Button 
                size="sm"
                onClick={handleSaveDashboard}
                disabled={saveDashboardMutation.isPending}
                data-testid="button-save-dashboard"
              >
                <Save className="w-4 h-4 mr-2" />
                {saveDashboardMutation.isPending ? "Saving..." : "Save"}
              </Button>
            </div>
          </div>
        </div>
      )}

      <div 
        ref={canvasRef}
        className="flex-1 dashboard-canvas relative bg-muted/30 m-4 rounded-lg overflow-auto"
        style={{ 
          backgroundImage: "radial-gradient(circle, hsl(var(--muted)) 1px, transparent 1px)",
          backgroundSize: "20px 20px"
        }}
        data-testid="dashboard-canvas"
      >
        {isLoading ? (
          <div className="p-4">
            <div className="text-center text-muted-foreground">Loading cards...</div>
          </div>
        ) : (cards as DashboardCardType[]).length === 0 ? (
          <div className="flex items-center justify-center h-full">
            <div className="text-center">
              <Plus className="w-12 h-12 text-muted-foreground mx-auto mb-4" />
              <h3 className="text-lg font-medium text-foreground mb-2">No cards yet</h3>
              <p className="text-muted-foreground mb-4">Add your first card to start visualizing data</p>
              <Button onClick={handleAddCard} data-testid="button-add-first-card">
                Add Card
              </Button>
            </div>
          </div>
        ) : (
          (cards as DashboardCardType[]).map((card: DashboardCardType) => (
            <DashboardCard
              key={card.id}
              card={card}
              onPositionChange={handleCardPositionChange}
              onEdit={handleEditCard}
            />
          ))
        )}
      </div>

      <Dialog open={isAddCardDialogOpen} onOpenChange={setIsAddCardDialogOpen}>
        <DialogContent className="sm:max-w-2xl max-h-[90vh] overflow-y-auto">
          <DialogHeader>
            <DialogTitle>Add Dashboard Card</DialogTitle>
          </DialogHeader>
          <form onSubmit={handleCreateCard} className="space-y-4">
            <div className="space-y-2">
              <Label htmlFor="cardTitle">Card Title</Label>
              <Input 
                id="cardTitle" 
                placeholder="Sales Overview" 
                value={cardTitle}
                onChange={(e) => setCardTitle(e.target.value)}
                data-testid="input-card-title"
                required
              />
            </div>

            <div className="space-y-2">
              <Label>Data Source (Optional)</Label>
              <Select value={selectedDataSource} onValueChange={setSelectedDataSource}>
                <SelectTrigger data-testid="select-data-source">
                  <SelectValue placeholder="Select a data source" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="none">No data source</SelectItem>
                  {(dataSources as DataSource[]).map((dataSource) => (
                    <SelectItem key={dataSource.id} value={dataSource.id}>
                      {dataSource.name}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            <div className="space-y-3">
              <Label>Visualization Type</Label>
              <div className="grid grid-cols-1 sm:grid-cols-3 gap-3">
                {visualizationTypes.map((type) => {
                  const IconComponent = type.icon;
                  return (
                    <Button
                      key={type.id}
                      type="button"
                      variant={selectedVisualizationType === type.id ? "default" : "outline"}
                      className="h-auto p-4 flex flex-col sm:flex-col items-center justify-center space-y-2 min-h-[80px] text-left"
                      onClick={() => setSelectedVisualizationType(type.id)}
                      data-testid={`button-visualization-${type.id}`}
                    >
                      <IconComponent className="w-6 h-6 flex-shrink-0" />
                      <div className="text-center w-full">
                        <div className="text-sm font-medium leading-tight">{type.name}</div>
                        <div className="text-xs text-muted-foreground leading-tight mt-1 overflow-hidden">{type.description}</div>
                      </div>
                    </Button>
                  );
                })}
              </div>
            </div>

            <div className="flex flex-col sm:flex-row space-y-2 sm:space-y-0 sm:space-x-3 pt-4">
              <Button 
                type="button" 
                variant="outline" 
                className="flex-1"
                onClick={() => setIsAddCardDialogOpen(false)}
                data-testid="button-cancel-card"
              >
                Cancel
              </Button>
              <Button 
                type="submit" 
                className="flex-1"
                disabled={createCardMutation.isPending}
                data-testid="button-submit-card"
              >
                <Plus className="w-4 h-4 mr-2" />
                {createCardMutation.isPending ? "Creating..." : "Create Card"}
              </Button>
            </div>
          </form>
        </DialogContent>
      </Dialog>

      <Dialog open={isEditCardDialogOpen} onOpenChange={setIsEditCardDialogOpen}>
        <DialogContent className="sm:max-w-2xl max-h-[90vh] overflow-y-auto">
          <DialogHeader>
            <DialogTitle>Edit Dashboard Card</DialogTitle>
          </DialogHeader>
          <form onSubmit={handleUpdateCard} className="space-y-4">
            <div className="space-y-2">
              <Label htmlFor="editCardTitle">Card Title</Label>
              <Input 
                id="editCardTitle" 
                placeholder="Sales Overview" 
                value={cardTitle}
                onChange={(e) => setCardTitle(e.target.value)}
                data-testid="input-edit-card-title"
                required
              />
            </div>

            <div className="space-y-2">
              <Label>Data Source (Optional)</Label>
              <Select value={selectedDataSource} onValueChange={setSelectedDataSource}>
                <SelectTrigger data-testid="select-edit-data-source">
                  <SelectValue placeholder="Select a data source" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="none">No data source</SelectItem>
                  {(dataSources as DataSource[]).map((dataSource) => (
                    <SelectItem key={dataSource.id} value={dataSource.id}>
                      {dataSource.name}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            <div className="space-y-3">
              <Label>Visualization Type</Label>
              <div className="grid grid-cols-1 sm:grid-cols-3 gap-3">
                {visualizationTypes.map((type) => {
                  const IconComponent = type.icon;
                  return (
                    <Button
                      key={type.id}
                      type="button"
                      variant={selectedVisualizationType === type.id ? "default" : "outline"}
                      className="h-auto p-4 flex flex-col sm:flex-col items-center justify-center space-y-2 min-h-[80px] text-left"
                      onClick={() => setSelectedVisualizationType(type.id)}
                      data-testid={`button-edit-visualization-${type.id}`}
                    >
                      <IconComponent className="w-6 h-6 flex-shrink-0" />
                      <div className="text-center w-full">
                        <div className="text-sm font-medium leading-tight">{type.name}</div>
                        <div className="text-xs text-muted-foreground leading-tight mt-1 overflow-hidden">{type.description}</div>
                      </div>
                    </Button>
                  );
                })}
              </div>
            </div>

            <div className="flex flex-col sm:flex-row space-y-2 sm:space-y-0 sm:space-x-3 pt-4">
              <Button 
                type="button" 
                variant="outline" 
                className="flex-1"
                onClick={() => setIsEditCardDialogOpen(false)}
                data-testid="button-cancel-edit-card"
              >
                Cancel
              </Button>
              <Button 
                type="submit" 
                className="flex-1"
                disabled={editCardMutation.isPending}
                data-testid="button-submit-edit-card"
              >
                {editCardMutation.isPending ? "Updating..." : "Update Card"}
              </Button>
            </div>
          </form>
        </DialogContent>
      </Dialog>
    </div>
  );
}

import { useState, useRef, useEffect } from "react";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { ChartRenderer } from "@/components/charts/chart-renderer";
import { Settings, RotateCcw, X } from "lucide-react";
import { cn } from "@/lib/utils";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import type { DashboardCard as DashboardCardType } from "@shared/schema";

interface DashboardCardProps {
  card: DashboardCardType;
  onPositionChange: (cardId: string, position: { x: number; y: number }, size?: { width: number; height: number }) => void;
  onEdit?: (card: DashboardCardType) => void;
}

export function DashboardCard({ card, onPositionChange, onEdit }: DashboardCardProps) {
  const [isDragging, setIsDragging] = useState(false);
  const [isResizing, setIsResizing] = useState(false);
  const [position, setPosition] = useState(card.position as { x: number; y: number });
  const [size, setSize] = useState(card.size as { width: number; height: number });
  const dragRef = useRef<HTMLDivElement>(null);
  const dragStart = useRef<{ x: number; y: number } | null>(null);
  const resizeStart = useRef<{ x: number; y: number; width: number; height: number } | null>(null);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const handleMouseDown = (e: React.MouseEvent) => {
    const target = e.target as HTMLElement;

    // Don't start dragging if clicking on buttons or resize handles
    if (target.closest('button') || target.classList.contains('resize-handle')) {
      return;
    }

    e.preventDefault();
    setIsDragging(true);
    dragStart.current = {
      x: e.clientX - position.x,
      y: e.clientY - position.y,
    };
  };

  const handleMouseMove = (e: MouseEvent) => {
    if (isDragging && dragStart.current) {
      const newX = Math.max(0, e.clientX - dragStart.current.x);
      const newY = Math.max(0, e.clientY - dragStart.current.y);

      // Snap to 20px grid
      const snappedX = Math.round(newX / 20) * 20;
      const snappedY = Math.round(newY / 20) * 20;

      setPosition({ x: snappedX, y: snappedY });
    }
  };

  const handleMouseUp = () => {
    if (isDragging) {
      setIsDragging(false);
      onPositionChange(card.id, position);
    }
    if (isResizing) {
      setIsResizing(false);
      onPositionChange(card.id, position, size);
    }
  };

  // Add resize functionality
  const handleResizeStart = (e: React.MouseEvent) => {
    e.preventDefault();
    e.stopPropagation();
    setIsResizing(true);
    resizeStart.current = {
      x: e.clientX,
      y: e.clientY,
      width: size.width,
      height: size.height,
    };
  };

  const handleResizeMove = (e: MouseEvent) => {
    if (isResizing && resizeStart.current) {
      const deltaX = e.clientX - resizeStart.current.x;
      const deltaY = e.clientY - resizeStart.current.y;

      const newWidth = Math.max(200, resizeStart.current.width + deltaX);
      const newHeight = Math.max(150, resizeStart.current.height + deltaY);

      // Snap to 20px grid
      const snappedWidth = Math.round(newWidth / 20) * 20;
      const snappedHeight = Math.round(newHeight / 20) * 20;

      setSize({ width: snappedWidth, height: snappedHeight });
    }
  };

  // Add global mouse event listeners when dragging or resizing
  useEffect(() => {
    const handleMove = (e: MouseEvent) => {
      if (isDragging) {
        handleMouseMove(e);
      } else if (isResizing) {
        handleResizeMove(e);
      }
    };

    if (isDragging || isResizing) {
      document.addEventListener("mousemove", handleMove);
      document.addEventListener("mouseup", handleMouseUp);

      return () => {
        document.removeEventListener("mousemove", handleMove);
        document.removeEventListener("mouseup", handleMouseUp);
      };
    }
  }, [isDragging, isResizing, position, size]);

  const deleteCardMutation = useMutation({
    mutationFn: () => apiRequest("DELETE", `/api/cards/${card.id}`),
    onSuccess: () => {
      toast({
        title: "Card deleted",
        description: "Dashboard card has been deleted successfully.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/dashboards", card.dashboardId, "cards"] });
    },
    onError: () => {
      toast({
        title: "Failed to delete card",
        description: "Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleRefresh = () => {
    // Refresh chart data by invalidating queries
    queryClient.invalidateQueries({ queryKey: ["/api/data-sources", card.dataSourceId, "data"] });
    toast({
      title: "Card refreshed",
      description: "Dashboard card data has been refreshed.",
    });
  };

  const handleEdit = () => {
    if (onEdit) {
      onEdit(card);
    }
  };

  const handleDelete = () => {
    if (confirm("Are you sure you want to delete this card?")) {
      deleteCardMutation.mutate();
    }
  };

  return (
    <div
      ref={dragRef}
      className={cn(
        "absolute bg-card border border-border rounded-lg shadow-sm transition-all duration-200",
        isDragging ? "cursor-grabbing z-50 shadow-lg" : "cursor-move hover:shadow-md"
      )}
      style={{
        top: `${position.y}px`,
        left: `${position.x}px`,
        width: `${size.width}px`,
        height: `${size.height}px`,
      }}
      onMouseDown={handleMouseDown}
      data-testid={`dashboard-card-${card.id}`}
    >
      <div className="p-4 h-full flex flex-col">
        <div className="flex items-center justify-between mb-3">
          <h4 className="font-medium text-foreground truncate" data-testid={`text-card-title-${card.id}`}>
            {card.title}
          </h4>
          <div className="flex space-x-1">
            <Button 
              variant="ghost" 
              size="sm" 
              className="text-muted-foreground hover:text-foreground p-1 h-6 w-6"
              onClick={handleEdit}
              data-testid={`button-edit-card-${card.id}`}
            >
              <Settings className="w-3 h-3" />
            </Button>
            <Button 
              variant="ghost" 
              size="sm" 
              className="text-muted-foreground hover:text-foreground p-1 h-6 w-6"
              onClick={handleRefresh}
              data-testid={`button-refresh-card-${card.id}`}
            >
              <RotateCcw className="w-3 h-3" />
            </Button>
            <Button 
              variant="ghost" 
              size="sm" 
              className="text-muted-foreground hover:text-destructive p-1 h-6 w-6"
              onClick={handleDelete}
              data-testid={`button-delete-card-${card.id}`}
            >
              <X className="w-3 h-3" />
            </Button>
          </div>
        </div>
        <div className="flex-1 min-h-0">
          <ChartRenderer 
            type={card.visualizationType}
            config={card.config as any}
            dataSourceId={card.dataSourceId}
          />
        </div>
      </div>

      {/* Resize handle */}
      <div
        className="resize-handle absolute bottom-0 right-0 w-4 h-4 bg-muted-foreground/20 hover:bg-muted-foreground/40 cursor-se-resize"
        style={{
          clipPath: 'polygon(100% 0%, 0% 100%, 100% 100%)'
        }}
        onMouseDown={handleResizeStart}
      />
    </div>
  );
}

